import React, { useState, useEffect } from 'react';
import { ListGroup, Spinner } from 'react-bootstrap';

const AvailableDocuments = () => {
    const [documentList, setDocumentList] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchDocuments = async () => {
            try {
                const response = await fetch('http://localhost:8000/get_documents/');
                const data = await response.json();
                setDocumentList(data.data);
            } catch (error) {
                console.error('Error fetching documents:', error);
                setError('Failed to load documents');
            } finally {
                setLoading(false);
            }
        };

        fetchDocuments();
    }, []);

    return (
        <div className="content-section mt-4">
            <h3>Available Documents</h3>
            {loading ? (
                <div className="text-center">
                    <Spinner animation="border" variant="light" />
                </div>
            ) : error ? (
                <div className="alert alert-danger">{error}</div>
            ) : documentList.length === 0 ? (
                <p className="text-muted">No documents available</p>
            ) : (
                <ListGroup variant="flush">
                    {documentList.map((doc, idx) => (
                        <ListGroup.Item key={idx} className="bg-transparent text-white">
                            {doc}
                        </ListGroup.Item>
                    ))}
                </ListGroup>
            )}
        </div>
    );
};

export default AvailableDocuments;
