// src/components/WorkoutPlanner/WorkoutPlannerForm.js

import React, { useState } from 'react';
import axios from 'axios';
import { Modal, Spinner, Card } from 'react-bootstrap';

const WorkoutPlannerForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    age: '',
    gender: '',
    height: '',
    weight: '',
    occupation: '',
    experience_level: 'Beginner'
  });

  // Add new states for the intermediate step
  const [showAnalysisStep, setShowAnalysisStep] = useState(false);
  const [physicalAnalysis, setPhysicalAnalysis] = useState(null);
  const [additionalInfo, setAdditionalInfo] = useState('');
  const [analyzingCondition, setAnalyzingCondition] = useState(false);

  const [workoutPlan, setWorkoutPlan] = useState(null);
  const [adjustmentInput, setAdjustmentInput] = useState('');
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [selectedExercise, setSelectedExercise] = useState(null);
  const [exerciseExplanation, setExerciseExplanation] = useState('');
  const [showExplanationModal, setShowExplanationModal] = useState(false);
  const [loadingExplanation, setLoadingExplanation] = useState(false);
  const [explanationCache, setExplanationCache] = useState({});

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Update the handleSubmit to show analysis first, not directly generate plan
  const handleSubmit = async (e) => {
    e.preventDefault();
    setAnalyzingCondition(true);
    setError(null);

    const data = new FormData();
    Object.keys(formData).forEach((key) => {
      data.append(key, formData[key]);
    });

    try {
      // Make a new API call to get physical analysis
      const response = await axios.post('http://localhost:8000/analyze_physical_condition', data, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      if (response.data.error) {
        setError(response.data.error);
      } else {
        setPhysicalAnalysis(response.data.analysis);
        setShowAnalysisStep(true);
      }
    } catch (error) {
      if (error.response?.data?.error) {
        setError(error.response.data.error);
      } else {
        setError('An error occurred while analyzing your information. Please try again.');
      }
    } finally {
      setAnalyzingCondition(false);
    }
  };

  // New function to generate the final plan with additional information
  const handleFinalPlanGeneration = async () => {
    setLoading(true);
    setError(null);

    const data = new FormData();
    Object.keys(formData).forEach((key) => {
      data.append(key, formData[key]);
    });
    data.append('additional_info', additionalInfo);
    data.append('analysis', JSON.stringify(physicalAnalysis));

    try {
      const response = await axios.post('http://localhost:8000/generate_workout_plan_detailed', data, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      if (response.data.error) {
        setError(response.data.error);
        setWorkoutPlan(null);
      } else {
        setWorkoutPlan(response.data.plan);
        setShowAnalysisStep(false);
      }
    } catch (error) {
      if (error.response?.data?.error) {
        setError(error.response.data.error);
      } else {
        setError('An error occurred. Please try again.');
      }
      setWorkoutPlan(null);
    } finally {
      setLoading(false);
    }
  };

  // Skip the analysis step and proceed with basic info only
  const handleSkipAnalysis = () => {
    setLoading(true);

    const data = new FormData();
    Object.keys(formData).forEach((key) => {
      data.append(key, formData[key]);
    });

    axios.post('http://localhost:8000/generate_workout_plan', data, {
      headers: {
        'Content-Type': 'multipart/form-data',
      },
    })
      .then((response) => {
        if (response.data.error) {
          setError(response.data.error);
          setWorkoutPlan(null);
        } else {
          setWorkoutPlan(response.data.plan);
          setShowAnalysisStep(false);
        }
      })
      .catch((error) => {
        if (error.response?.data?.error) {
          setError(error.response.data.error);
        } else {
          setError('An error occurred. Please try again.');
        }
        setWorkoutPlan(null);
      })
      .finally(() => {
        setLoading(false);
      });
  };

  const handleAdjustPlan = async (e) => {
    e.preventDefault();
    if (!adjustmentInput.trim()) return;

    setLoading(true);
    const data = new FormData();
    data.append('name', formData.name);
    data.append('adjustment_text', adjustmentInput);
    data.append('current_plan', JSON.stringify(workoutPlan));

    try {
      const response = await axios.post('http://localhost:8000/adjust_workout_plan', data, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      if (response.data.error) {
        setError(response.data.error);
      } else {
        setWorkoutPlan(response.data.plan);
        setAdjustmentInput('');
        setError(null);
      }
    } catch (error) {
      setError(error.response?.data?.error || 'An error occurred. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleExerciseClick = async (day, exercise) => {
    setSelectedExercise({ day, exercise });
    setShowExplanationModal(true);

    const cacheKey = `${day}-${exercise}`;

    if (explanationCache[cacheKey]) {
      setExerciseExplanation(explanationCache[cacheKey]);
      return;
    }

    setLoadingExplanation(true);

    const data = new FormData();
    data.append('name', formData.name);
    data.append('day', day);
    data.append('exercise', exercise);
    data.append('current_plan', JSON.stringify(workoutPlan));
    data.append('user_data', JSON.stringify(formData));

    try {
      const response = await axios.post('http://localhost:8000/explain_exercise', data, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      if (response.data.error) {
        setError(response.data.error);
      } else {
        const explanation = response.data.explanation;
        setExplanationCache(prev => ({
          ...prev,
          [cacheKey]: explanation
        }));
        setExerciseExplanation(explanation);
      }
    } catch (error) {
      setError(error.response?.data?.error || 'An error occurred while getting the explanation.');
    } finally {
      setLoadingExplanation(false);
    }
  };

  const renderWorkoutPlan = (plan) => {
    return (
      <div className="workout-plan-container" style={{
        border: '1px solid #ccc',
        padding: '1rem',
        borderRadius: '8px',
        marginTop: '1rem'
      }}>
        {Object.entries(plan).map(([day, exercises]) => (
          <div key={day} style={{ marginBottom: '1rem' }}>
            <h2 style={{ marginBottom: '0.5rem' }}>{day}</h2>
            <ul style={{ paddingLeft: '1.2rem' }}>
              {exercises.map((ex, i) => (
                <li 
                  key={i} 
                  style={{ 
                    marginBottom: '0.25rem',
                    cursor: 'pointer'
                  }}
                  onDoubleClick={() => handleExerciseClick(day, typeof ex === 'object' ? JSON.stringify(ex) : ex)}
                  className="exercise-item"
                >
                  {typeof ex === 'object'
                    ? Object.entries(ex).map(([key, value]) => (
                        <div key={key}>{key}: {value}</div>
                      ))
                    : ex
                  }
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    );
  };

  // New function to render the physical analysis step
  const renderAnalysisStep = () => {
    return (
      <div className="analysis-step-container">
        <h1>Your Physical Analysis</h1>
        <Card className="mb-4 bg-dark text-white">
          <Card.Body>
            <Card.Title>Based on your information</Card.Title>
            <div className="analysis-details">
              {Object.entries(physicalAnalysis).map(([key, value]) => (
                <p key={key}><strong>{key}:</strong> {value}</p>
              ))}
            </div>
          </Card.Body>
        </Card>

        <div className="additional-info-section mb-4">
          <h3>Would you like to add any additional information?</h3>
          <p className="text-muted">
            This could include injuries, specific goals, preferences, or any other relevant health information.
          </p>
          <textarea
            className="form-control mt-2 mb-3"
            rows={4}
            value={additionalInfo}
            onChange={(e) => setAdditionalInfo(e.target.value)}
            placeholder="Example: I have a lower back injury, I prefer morning workouts, I want to focus on mobility..."
          />

          <div className="d-flex justify-content-between mt-3">
            <button 
              onClick={handleSkipAnalysis} 
              className="btn btn-secondary"
              disabled={loading}
            >
              Generate Basic Plan
            </button>
            <button 
              onClick={handleFinalPlanGeneration} 
              className="btn btn-primary"
              disabled={loading}
            >
              {loading ? (
                <>
                  <Spinner as="span" animation="border" size="sm" role="status" aria-hidden="true" />
                  <span className="ms-2">Generating...</span>
                </>
              ) : "Generate Detailed Plan"}
            </button>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div>
      {showAnalysisStep && physicalAnalysis ? (
        renderAnalysisStep()
      ) : !workoutPlan && !loading ? (
        <form onSubmit={handleSubmit} style={{ padding: '1rem', maxWidth: '500px' }}>
          <h1>Workout Planner</h1>
          <p className="text-muted mb-4">
            Fill out the form below to get a personalized workout plan that fits your physical condition and goals.
          </p>

          <div style={{ marginBottom: '1rem' }}>
            <label style={{ display: 'block', fontWeight: 'bold' }}>Name:</label>
            <input
              type="text"
              name="name"
              required
              value={formData.name}
              onChange={handleChange}
              placeholder="Enter your name"
              style={{ width: '100%', padding: '0.5rem' }}
            />
          </div>

          <div style={{ marginBottom: '1rem' }}>
            <label style={{ display: 'block', fontWeight: 'bold' }}>Age:</label>
            <input
              type="number"
              name="age"
              required
              value={formData.age}
              onChange={handleChange}
              placeholder="e.g. 30"
              style={{ width: '100%', padding: '0.5rem' }}
            />
          </div>

          <div style={{ marginBottom: '1rem' }}>
            <label style={{ display: 'block', fontWeight: 'bold' }}>Gender:</label>
            <select
              name="gender"
              required
              value={formData.gender}
              onChange={handleChange}
              style={{ width: '100%', padding: '0.5rem' }}
            >
              <option value="">Select gender</option>
              <option value="M">Male</option>
              <option value="F">Female</option>
            </select>
          </div>

          <div style={{ marginBottom: '1rem' }}>
            <label style={{ display: 'block', fontWeight: 'bold' }}>Height (cm):</label>
            <input
              type="number"
              name="height"
              required
              value={formData.height}
              onChange={handleChange}
              placeholder="e.g. 175"
              style={{ width: '100%', padding: '0.5rem' }}
            />
          </div>

          <div style={{ marginBottom: '1rem' }}>
            <label style={{ display: 'block', fontWeight: 'bold' }}>Weight (kg):</label>
            <input
              type="number"
              name="weight"
              required
              value={formData.weight}
              onChange={handleChange}
              placeholder="e.g. 70"
              style={{ width: '100%', padding: '0.5rem' }}
            />
          </div>

          <div style={{ marginBottom: '1rem' }}>
            <label style={{ display: 'block', fontWeight: 'bold' }}>Occupation:</label>
            <input
              type="text"
              name="occupation"
              required
              value={formData.occupation}
              onChange={handleChange}
              placeholder="e.g. Software Developer"
              style={{ width: '100%', padding: '0.5rem' }}
            />
          </div>

          <div style={{ marginBottom: '1rem' }}>
            <label style={{ display: 'block', fontWeight: 'bold' }}>Experience Level:</label>
            <select
              name="experience_level"
              required
              value={formData.experience_level}
              onChange={handleChange}
              style={{ width: '100%', padding: '0.5rem' }}
            >
              <option value="Beginner">Beginner</option>
              <option value="Intermediate">Intermediate</option>
              <option value="Advanced">Advanced</option>
            </select>
          </div>

          <button 
            type="submit" 
            style={{ 
              padding: '0.8rem 1.2rem',
              backgroundColor: '#007bff',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
            disabled={analyzingCondition}
          >
            {analyzingCondition ? (
              <>
                <Spinner as="span" animation="border" size="sm" role="status" aria-hidden="true" />
                <span className="ms-2">Analyzing...</span>
              </>
            ) : "Analyze My Condition"}
          </button>
        </form>
      ) : loading && !workoutPlan ? (
        <div className="text-center mt-4">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="mt-2">
            {adjustmentInput ? 'Adjusting your plan...' : 'Generating your plan...'}
          </p>
        </div>
      ) : (
        <div style={{ marginTop: '1rem' }}>
          <h1>Your Workout Plan</h1>
          {renderWorkoutPlan(workoutPlan)}

          {/* Plan Adjustment Form */}
          <div className="content-section mt-4">
            <h3>Adjust Your Plan</h3>
            <form onSubmit={handleAdjustPlan}>
              <div className="mb-3">
                <textarea
                  className="form-control"
                  value={adjustmentInput}
                  onChange={(e) => setAdjustmentInput(e.target.value)}
                  placeholder="Describe how you'd like to adjust your plan (e.g., 'I need more rest days' or 'I want to focus more on upper body')"
                  rows={4}
                />
              </div>
              <button 
                type="submit"
                className="btn btn-primary w-100"
                disabled={!adjustmentInput.trim() || loading}
              >
                {loading ? 'Adjusting Plan...' : 'Adjust Plan'}
              </button>
            </form>
          </div>

          <button 
            onClick={() => {
              setWorkoutPlan(null);
              setAdjustmentInput('');
              setError(null);
              setShowAnalysisStep(false);
              setPhysicalAnalysis(null);
              setAdditionalInfo('');
            }} 
            className="btn btn-secondary mt-3"
          >
            Create New Plan
          </button>
        </div>
      )}

      {error && (
        <div className="alert alert-danger mt-3">
          {error}
        </div>
      )}

      {/* Exercise Explanation Modal */}
      <Modal
        show={showExplanationModal}
        onHide={() => {
          setShowExplanationModal(false);
          setSelectedExercise(null);
          setExerciseExplanation('');
        }}
        size="lg"
      >
        <Modal.Header closeButton>
          <Modal.Title>
            Exercise Explanation
            {selectedExercise && (
              <div className="text-muted fs-6">
                {selectedExercise.day} - {selectedExercise.exercise}
              </div>
            )}
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {loadingExplanation ? (
            <div className="text-center py-4">
              <div className="spinner-border text-primary" role="status">
                <span className="visually-hidden">Loading...</span>
              </div>
              <p className="mt-2">Getting exercise explanation...</p>
            </div>
          ) : (
            <div className="exercise-explanation">
              {exerciseExplanation}
            </div>
          )}
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default WorkoutPlannerForm;
