// src/components/WorkoutPlanner/WorkoutPlannerForm.js

import React, { useState } from 'react';
import axios from 'axios';
import { Modal } from 'react-bootstrap';

const WorkoutPlannerForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    age: '',
    gender: '',
    height: '',
    weight: '',
    occupation: '',
    experience_level: 'Beginner'
  });

  const [workoutPlan, setWorkoutPlan] = useState(null);
  const [adjustmentInput, setAdjustmentInput] = useState('');
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [selectedExercise, setSelectedExercise] = useState(null);
  const [exerciseExplanation, setExerciseExplanation] = useState('');
  const [showExplanationModal, setShowExplanationModal] = useState(false);
  const [loadingExplanation, setLoadingExplanation] = useState(false);
  const [explanationCache, setExplanationCache] = useState({});
  const [step, setStep] = useState('form'); // 'form', 'assessment', or 'plan'
  const [assessment, setAssessment] = useState(null);
  const [userFeedback, setUserFeedback] = useState('');

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const getInitialAssessment = async (userData) => {
    setLoading(true);
    try {
      const data = new FormData();
      Object.keys(userData).forEach((key) => {
        data.append(key, userData[key]);
      });

      const response = await axios.post('http://localhost:8000/initial_assessment', data, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      setAssessment(response.data.assessment);
      setStep('assessment');
    } catch (error) {
      setError(error.response?.data?.error || 'An error occurred during assessment.');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    getInitialAssessment(formData);
  };

  const generateFinalPlan = async () => {
    setLoading(true);
    try {
      const data = new FormData();
      Object.keys(formData).forEach((key) => {
        data.append(key, formData[key]);
      });
      data.append('user_feedback', userFeedback);

      const response = await axios.post('http://localhost:8000/generate_workout_plan', data, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      if (response.data.error) {
        setError(response.data.error);
        setWorkoutPlan(null);
      } else {
        setWorkoutPlan(response.data.plan);
        setStep('plan');
        setError(null);
      }
    } catch (error) {
      setError(error.response?.data?.error || 'An error occurred. Please try again.');
      setWorkoutPlan(null);
    } finally {
      setLoading(false);
    }
  };

  const handleAdjustPlan = async (e) => {
    e.preventDefault();
    if (!adjustmentInput.trim()) return;

    setLoading(true);
    const data = new FormData();
    data.append('name', formData.name);
    data.append('adjustment_text', adjustmentInput);
    data.append('current_plan', JSON.stringify(workoutPlan));

    try {
      const response = await axios.post('http://localhost:8000/adjust_workout_plan', data, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      if (response.data.error) {
        setError(response.data.error);
      } else {
        setWorkoutPlan(response.data.plan);
        setAdjustmentInput('');
        setError(null);
      }
    } catch (error) {
      setError(error.response?.data?.error || 'An error occurred. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleExerciseClick = async (day, exercise) => {
    setSelectedExercise({ day, exercise });
    setShowExplanationModal(true);

    const cacheKey = `${day}-${exercise}`;

    if (explanationCache[cacheKey]) {
      setExerciseExplanation(explanationCache[cacheKey]);
      return;
    }

    setLoadingExplanation(true);

    const data = new FormData();
    data.append('name', formData.name);
    data.append('day', day);
    data.append('exercise', exercise);
    data.append('current_plan', JSON.stringify(workoutPlan));
    data.append('user_data', JSON.stringify(formData));

    try {
      const response = await axios.post('http://localhost:8000/explain_exercise', data, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      if (response.data.error) {
        setError(response.data.error);
      } else {
        const explanation = response.data.explanation;
        setExplanationCache(prev => ({
          ...prev,
          [cacheKey]: explanation
        }));
        setExerciseExplanation(explanation);
      }
    } catch (error) {
      setError(error.response?.data?.error || 'An error occurred while getting the explanation.');
    } finally {
      setLoadingExplanation(false);
    }
  };

  const renderWorkoutPlan = (plan) => {
    return (
      <div className="workout-plan-container" style={{
        border: '1px solid #ccc',
        padding: '1rem',
        borderRadius: '8px',
        marginTop: '1rem'
      }}>
        {Object.entries(plan).map(([day, exercises]) => (
          <div key={day} style={{ marginBottom: '1rem' }}>
            <h2 style={{ marginBottom: '0.5rem' }}>{day}</h2>
            <ul style={{ paddingLeft: '1.2rem' }}>
              {exercises.map((ex, i) => (
                <li 
                  key={i} 
                  style={{ 
                    marginBottom: '0.25rem',
                    cursor: 'pointer'
                  }}
                  onDoubleClick={() => handleExerciseClick(day, typeof ex === 'object' ? JSON.stringify(ex) : ex)}
                  className="exercise-item"
                >
                  {typeof ex === 'object'
                    ? Object.entries(ex).map(([key, value]) => (
                        <div key={key}>{key}: {value}</div>
                      ))
                    : ex
                  }
                </li>
              ))}
            </ul>
          </div>
        ))}
      </div>
    );
  };

  const renderAssessment = () => {
    return (
      <div className="assessment-container" style={{ maxWidth: '600px', margin: '0 auto' }}>
        <h2>Initial Assessment</h2>

        <div className="assessment-content" style={{ 
          backgroundColor: '#333',
          padding: '1.5rem',
          borderRadius: '8px',
          marginBottom: '1.5rem'
        }}>
          <h3>Based on your information:</h3>
          <div style={{ whiteSpace: 'pre-line' }}>{assessment}</div>
        </div>

        <div className="feedback-section">
          <h3>Would you like to add any additional information?</h3>
          <p className="text-muted">
            For example: previous injuries, specific goals, preferred exercises, 
            or any other relevant health conditions.
          </p>
          <textarea
            className="form-control mb-3"
            value={userFeedback}
            onChange={(e) => setUserFeedback(e.target.value)}
            placeholder="Add your feedback here (optional)"
            rows={4}
          />

          <div className="d-flex justify-content-between">
            <button
              className="btn btn-secondary"
              onClick={() => setStep('form')}
            >
              Back
            </button>
            <button
              className="btn btn-primary"
              onClick={generateFinalPlan}
            >
              Generate Workout Plan
            </button>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div>
      {step === 'form' && !loading ? (
        <form onSubmit={handleSubmit} style={{ padding: '1rem', maxWidth: '500px' }}>
          <div style={{ marginBottom: '1rem' }}>
            <label style={{ display: 'block', fontWeight: 'bold' }}>Name:</label>
            <input
              type="text"
              name="name"
              required
              value={formData.name}
              onChange={handleChange}
              placeholder="Enter your name"
              style={{ width: '100%', padding: '0.5rem' }}
            />
          </div>

          <div style={{ marginBottom: '1rem' }}>
            <label style={{ display: 'block', fontWeight: 'bold' }}>Age:</label>
            <input
              type="number"
              name="age"
              required
              value={formData.age}
              onChange={handleChange}
              placeholder="e.g. 30"
              style={{ width: '100%', padding: '0.5rem' }}
            />
          </div>

          <div style={{ marginBottom: '1rem' }}>
            <label style={{ display: 'block', fontWeight: 'bold' }}>Gender:</label>
            <select
              name="gender"
              required
              value={formData.gender}
              onChange={handleChange}
              style={{ width: '100%', padding: '0.5rem' }}
            >
              <option value="">Select gender</option>
              <option value="M">Male</option>
              <option value="F">Female</option>
            </select>
          </div>

          <div style={{ marginBottom: '1rem' }}>
            <label style={{ display: 'block', fontWeight: 'bold' }}>Height (cm):</label>
            <input
              type="number"
              name="height"
              required
              value={formData.height}
              onChange={handleChange}
              placeholder="e.g. 175"
              style={{ width: '100%', padding: '0.5rem' }}
            />
          </div>

          <div style={{ marginBottom: '1rem' }}>
            <label style={{ display: 'block', fontWeight: 'bold' }}>Weight (kg):</label>
            <input
              type="number"
              name="weight"
              required
              value={formData.weight}
              onChange={handleChange}
              placeholder="e.g. 70"
              style={{ width: '100%', padding: '0.5rem' }}
            />
          </div>

          <div style={{ marginBottom: '1rem' }}>
            <label style={{ display: 'block', fontWeight: 'bold' }}>Occupation:</label>
            <input
              type="text"
              name="occupation"
              required
              value={formData.occupation}
              onChange={handleChange}
              placeholder="e.g. Software Developer"
              style={{ width: '100%', padding: '0.5rem' }}
            />
          </div>

          <div style={{ marginBottom: '1rem' }}>
            <label style={{ display: 'block', fontWeight: 'bold' }}>Experience Level:</label>
            <select
              name="experience_level"
              required
              value={formData.experience_level}
              onChange={handleChange}
              style={{ width: '100%', padding: '0.5rem' }}
            >
              <option value="Beginner">Beginner</option>
              <option value="Intermediate">Intermediate</option>
              <option value="Advanced">Advanced</option>
            </select>
          </div>

          <button 
            type="submit" 
            style={{ 
              padding: '0.8rem 1.2rem',
              backgroundColor: '#007bff',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            Generate Workout Plan
          </button>
        </form>
      ) : step === 'assessment' && !loading ? (
        renderAssessment()
      ) : loading ? (
        <div className="text-center mt-4">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="mt-2">
            {step === 'form' ? 'Analyzing your information...' : 
             step === 'assessment' ? 'Generating your plan...' : 
             'Processing...'}
          </p>
        </div>
      ) : (
        <div style={{ marginTop: '1rem' }}>
          <h1>Your Workout Plan</h1>
          {renderWorkoutPlan(workoutPlan)}

          {/* Plan Adjustment Form */}
          <div className="content-section mt-4">
            <h3>Adjust Your Plan</h3>
            <form onSubmit={handleAdjustPlan}>
              <div className="mb-3">
                <textarea
                  className="form-control"
                  value={adjustmentInput}
                  onChange={(e) => setAdjustmentInput(e.target.value)}
                  placeholder="Describe how you'd like to adjust your plan (e.g., 'I need more rest days' or 'I want to focus more on upper body')"
                  rows={4}
                />
              </div>
              <button 
                type="submit"
                className="btn btn-primary w-100"
                disabled={!adjustmentInput.trim() || loading}
              >
                {loading ? 'Adjusting Plan...' : 'Adjust Plan'}
              </button>
            </form>
          </div>

          <button 
            onClick={() => {
              setWorkoutPlan(null);
              setAdjustmentInput('');
              setError(null);
            }} 
            className="btn btn-secondary mt-3"
          >
            Create New Plan
          </button>
        </div>
      )}

      {error && (
        <div className="alert alert-danger mt-3">
          {error}
        </div>
      )}

      {/* Exercise Explanation Modal */}
      <Modal
        show={showExplanationModal}
        onHide={() => {
          setShowExplanationModal(false);
          setSelectedExercise(null);
          setExerciseExplanation('');
        }}
        size="lg"
      >
        <Modal.Header closeButton>
          <Modal.Title>
            Exercise Explanation
            {selectedExercise && (
              <div className="text-muted fs-6">
                {selectedExercise.day} - {selectedExercise.exercise}
              </div>
            )}
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {loadingExplanation ? (
            <div className="text-center py-4">
              <div className="spinner-border text-primary" role="status">
                <span className="visually-hidden">Loading...</span>
              </div>
              <p className="mt-2">Getting exercise explanation...</p>
            </div>
          ) : (
            <div className="exercise-explanation">
              {exerciseExplanation}
            </div>
          )}
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default WorkoutPlannerForm;
